<script>
export default {
    name: "MPGResultTeam",
    data: function () {
        return {};
    },
    props: {
        teamName: {
            type: String,
        },
        finalTeam: {
            type: Array,
            required: true,
        },
        teamGoals: {
            type: Number,
            required: true,
        },
        opponentCsc: {
            type: Number,
            required: true,
        },
        mpgGoals: {
            type: Array,
            required: true,
        },
    },
    methods: {
        getPlayerNote: function (player) {
            if (player.note) {
                if (player.bonus) {
                    return player.note - player.bonus;
                }
                return player.note;
            }
            return "";
        },
        getNoteClass: function (note) {
            if (note < 4) {
                return "low";
            }
            if (note > 7) {
                return "high";
            }
            return;
        },
    },
};
</script>

